CREATE TABLE content_contentnode (id char(32) NOT NULL PRIMARY KEY, title varchar(200) NOT NULL, content_id char(32) NOT NULL, description varchar(400) NULL, sort_order real NULL, license_owner varchar(200) NOT NULL, author varchar(200) NOT NULL, kind varchar(200) NOT NULL, available bool NOT NULL, stemmed_metaphone varchar(1800) NOT NULL, lft integer unsigned NOT NULL, rght integer unsigned NOT NULL, tree_id integer unsigned NOT NULL, level integer unsigned NOT NULL, license_id integer NULL REFERENCES content_license (id), parent_id char(32) NULL REFERENCES content_contentnode (id));
CREATE TABLE content_assessmentmetadata (id char(32) NOT NULL PRIMARY KEY, assessment_item_ids text NOT NULL, number_of_assessments integer NOT NULL, mastery_model text NOT NULL, randomize bool NOT NULL, is_manipulable bool NOT NULL, contentnode_id char(32) NULL REFERENCES content_contentnode (id));

